import UIKit
import DACSDKMA


// --------------------------------------------------
// MARK: - class
// --------------------------------------------------
/// 画像クラス生成クラス
@objc
open class DACSDKMAAdImageGenerator: NSObject {

    /// 再生ボタン・アイコン画像
    static let playButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// 一時停止ボタン・アイコン画像
    static let pauseButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// リプレイボタン・アイコン画像
    static let replayButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// 音量ONボタン・アイコン画像
    static let volumeOnButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// 音量OFFボタン・アイコン画像
    static let volumeOffButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )

    /// 閉じるボタン・アイコン画像
    static let closeButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// インライン化ボタン・アイコン画像
    static let exitFullscreenButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// フルスクリーン化ボタン・アイコン画像を生成
    static let enterFullscreenButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// リンクボタン・アイコン画像を生成
    static let linkButtonIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// PR画像
    static let prIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /// スキップボタン・アイコン画像
    static let skipIconImage: UIImage? = DACSDKMAAdImageGenerator.generateImage(
        ""
    )
    
    /**
     base64文字列をUIImageに変換する。
     */
    private class func generateImage(_ base64String :String) -> UIImage? {
        if let decodedData = Data(base64Encoded: base64String, options: Data.Base64DecodingOptions.ignoreUnknownCharacters) {
            return UIImage (data: decodedData)
        }
        else {
            return nil
        }
    }
    
}
